from pybricks.hubs import PrimeHub
from pybricks.parameters import Direction, Port
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase
from pybricks.tools import read_input_byte

# Set up all devices.
prime_hub = PrimeHub()
right = Motor(Port.A, Direction.CLOCKWISE)
left = Motor(Port.B, Direction.COUNTERCLOCKWISE)
drive_base = DriveBase(left, right, 56, 114)

# Initialize variables.
key = 0


# The main program starts here.
while True:
    key = read_input_byte(True, False)
    if key is not None:
        print(key)
        drive_base.straight(0)
        if key == 65:
            drive_base.drive(400, 0)
        elif key == 66:
            drive_base.drive(-200, 0)
        elif key == 67:
            drive_base.drive(0, 360)
        elif key == 68:
            drive_base.drive(0, -360)
        else:
            pass
